import { motion, AnimatePresence } from 'framer-motion';
import { MapPin, Info } from 'lucide-react';
import { useFestival } from '@/context/FestivalContext';
import { getVenueStatus } from '@/data/venueData';
import { cn } from '@/lib/utils';
import { VenueIcon } from './VenueIcon';

const statusColors = {
  safe: 'bg-green-500',
  warning: 'bg-yellow-500',
  critical: 'bg-red-500',
};

const statusGlows = {
  safe: 'shadow-[0_0_20px_rgba(34,197,94,0.3)]',
  warning: 'shadow-[0_0_20px_rgba(234,179,8,0.3)]',
  critical: 'shadow-[0_0_30px_rgba(239,68,68,0.5)]',
};

// Map positions for the 6 venues
const VENUE_POSITIONS = [
  { top: '15%', left: '50%', labelId: 'main-stage' },     // Main Stage
  { top: '35%', left: '20%', labelId: 'dance-arena' },   // Dance Arena
  { top: '35%', left: '80%', labelId: 'food-court' },    // Food Court
  { top: '65%', left: '25%', labelId: 'gaming-zone' },   // Gaming Zone
  { top: '75%', left: '50%', labelId: 'music-lounge' },  // Music Lounge (swapped with art gallery pos for variety)
  { top: '65%', left: '75%', labelId: 'art-gallery' },   // Art Gallery
];

export function CrowdHeatmap() {
  const { venues } = useFestival();

  return (
    <motion.div
      initial={{ opacity: 0, scale: 0.95 }}
      animate={{ opacity: 1, scale: 1 }}
      className="glass-card p-6 border-indigo-500/20 shadow-indigo-500/10"
    >
      <div className="flex flex-col md:flex-row md:items-center justify-between gap-4 mb-6">
        <div className="flex items-center gap-3">
          <div className="w-10 h-10 rounded-lg bg-indigo-500/10 flex items-center justify-center border border-indigo-500/20">
            <MapPin className="w-5 h-5 text-indigo-500" />
          </div>
          <div>
            <h3 className="font-bold text-lg text-foreground tracking-tight">Live Crowd Heatmap</h3>
            <div className="flex items-center gap-1.5 leading-none">
              <span className="relative flex h-2 w-2">
                <span className="animate-ping absolute inline-flex h-full w-full rounded-full bg-red-400 opacity-75"></span>
                <span className="relative inline-flex rounded-full h-2 w-2 bg-red-500"></span>
              </span>
              <p className="text-xs text-muted-foreground font-medium uppercase tracking-wider">Live Ground Overview</p>
            </div>
          </div>
        </div>

        <div className="flex items-center gap-4 px-3 py-1.5 bg-muted/30 rounded-full border border-border/50">
          <div className="flex items-center gap-1.5">
            <div className="w-2.5 h-2.5 rounded-full bg-green-500" />
            <span className="text-[10px] font-bold text-muted-foreground uppercase">Safe</span>
          </div>
          <div className="flex items-center gap-1.5 border-x border-border/50 px-4">
            <div className="w-2.5 h-2.5 rounded-full bg-yellow-500" />
            <span className="text-[10px] font-bold text-muted-foreground uppercase">Warn</span>
          </div>
          <div className="flex items-center gap-1.5">
            <div className="w-2.5 h-2.5 rounded-full bg-red-500" />
            <span className="text-[10px] font-bold text-muted-foreground uppercase">Crit</span>
          </div>
        </div>
      </div>

      {/* Modern Interactive SVG Ground Map */}
      <div className="relative h-[600px] rounded-2xl overflow-hidden border border-border bg-[#0f172a] group/map shadow-inner">

        {/* SVG Decorative Ground & Paths */}
        <svg className="absolute inset-0 w-full h-full opacity-40 select-none pointer-events-none" viewBox="0 0 1000 600" preserveAspectRatio="none">
          <defs>
            <pattern id="grid" width="40" height="40" patternUnits="userSpaceOnUse">
              <path d="M 40 0 L 0 0 0 40" fill="none" stroke="white" strokeWidth="0.5" opacity="0.1" />
            </pattern>
            <radialGradient id="mapGlow" cx="50%" cy="50%" r="50%">
              <stop offset="0%" stopColor="#1e293b" />
              <stop offset="100%" stopColor="transparent" />
            </radialGradient>
          </defs>

          <rect width="1000" height="600" fill="url(#grid)" />

          {/* Connecting Path Lines */}
          <path
            d="M 500 100 L 200 200 M 500 100 L 800 200 M 200 200 L 250 400 M 800 200 L 750 400 M 250 400 L 500 450 M 750 400 L 500 450 M 500 100 L 500 450"
            stroke="#6366f1"
            strokeWidth="2"
            strokeDasharray="10 5"
            fill="none"
            className="opacity-20 animate-[dash_20s_linear_infinite]"
          />

          {/* Abstract Ground Shapes */}
          <path d="M 150 150 Q 300 100 450 150" stroke="#334155" fill="none" strokeWidth="1" strokeDasharray="5 5" />
          <path d="M 550 150 Q 700 100 850 150" stroke="#334155" fill="none" strokeWidth="1" strokeDasharray="5 5" />
          <circle cx="500" cy="300" r="150" stroke="#334155" fill="none" strokeWidth="1" strokeDasharray="10 10" />
        </svg>

        {/* Dynamic Heat Blobs behind venues */}
        <div className="absolute inset-0 overflow-hidden pointer-events-none">
          {venues.map((venue, index) => {
            const pos = VENUE_POSITIONS[index] || { top: '50%', left: '50%' };
            const percentage = Math.round((venue.currentCrowd / venue.capacity) * 100);
            const status = getVenueStatus(venue);
            const blobSize = 80 + (percentage * 2);

            return (
              <motion.div
                key={`blob-${venue.id}`}
                animate={{
                  scale: [1, 1.2, 1],
                  opacity: [0.1, 0.2, 0.1],
                }}
                transition={{
                  duration: 3 + index % 3,
                  repeat: Infinity,
                  ease: "easeInOut"
                }}
                className={cn(
                  "absolute rounded-full blur-[60px] transform -translate-x-1/2 -translate-y-1/2 transition-colors duration-1000",
                  status === 'safe' ? 'bg-green-500/20' :
                    status === 'warning' ? 'bg-yellow-500/30' :
                      'bg-red-500/40'
                )}
                style={{
                  top: pos.top,
                  left: pos.left,
                  width: blobSize,
                  height: blobSize
                } as any}
              />
            );
          })}
        </div>

        {/* Venue Markers */}
        <AnimatePresence>
          {venues.map((venue, index) => {
            const status = getVenueStatus(venue);
            const percentage = Math.round((venue.currentCrowd / venue.capacity) * 100);
            const pos = VENUE_POSITIONS[index] || { top: '50%', left: '50%' };

            return (
              <motion.div
                key={venue.id}
                initial={{ scale: 0, opacity: 0 }}
                animate={{ scale: 1, opacity: 1 }}
                transition={{ delay: index * 0.1, type: 'spring', damping: 15 }}
                className="absolute transform -translate-x-1/2 -translate-y-1/2 group z-20"
                style={{ top: pos.top, left: pos.left } as any}
              >
                {/* Critical Pulse */}
                {status === 'critical' && (
                  <div className="absolute inset-0 rounded-full bg-red-500/40 animate-ping -z-10 w-20 h-20 -m-6" />
                )}

                {/* Interactive Venue Node */}
                <div className="relative">
                  <motion.div
                    whileHover={{ scale: 1.15, rotate: 5 }}
                    whileTap={{ scale: 0.95 }}
                    className={cn(
                      'w-12 h-12 rounded-xl flex items-center justify-center p-2.5 transition-all duration-300 border-2 cursor-pointer shadow-2xl',
                      status === 'safe' ? 'bg-[#064e3b]/80 border-green-500/50' :
                        status === 'warning' ? 'bg-[#78350f]/80 border-yellow-500/50' :
                          'bg-[#7f1d1d]/80 border-red-500/50',
                      statusGlows[status]
                    )}
                  >
                    <VenueIcon icon={venue.icon} className={cn(
                      "w-full h-full drop-shadow-sm transition-transform group-hover:scale-110",
                      status === 'safe' ? 'text-green-400' :
                        status === 'warning' ? 'text-yellow-400' :
                          'text-red-400 font-bold'
                    )} />

                    {/* Tiny Status Indicator Dot */}
                    <div className={cn(
                      "absolute -top-1 -right-1 w-3.5 h-3.5 rounded-full border-2 border-[#0f172a] shadow-sm",
                      statusColors[status]
                    )} />
                  </motion.div>

                  {/* Label below marker */}
                  <div className="absolute top-full left-1/2 -translate-x-1/2 mt-3 pointer-events-none text-center">
                    <span className="text-[10px] font-bold text-white/90 bg-indigo-950/80 px-2 py-0.5 rounded-md backdrop-blur-sm border border-indigo-400/30 shadow-lg uppercase tracking-tight whitespace-nowrap">
                      {venue.name.split(' ')[0]}
                    </span>
                  </div>

                  {/* Pro Tooltip on Hover */}
                  <div className="absolute bottom-full left-1/2 -translate-x-1/2 mb-4 opacity-0 group-hover:opacity-100 transition-all duration-300 scale-90 group-hover:scale-100 pointer-events-none z-50 origin-bottom">
                    <div className="bg-[#1e1b4b] border border-indigo-400/40 p-3 rounded-xl shadow-[0_10px_40px_rgba(0,0,0,0.5)] min-w-[180px]">
                      <div className="flex items-center justify-between mb-2">
                        <span className="text-xs font-bold text-indigo-200">{venue.name}</span>
                        <div className={cn("px-1.5 py-0.5 rounded-sm text-[8px] font-black uppercase tracking-tighter",
                          status === 'safe' ? 'bg-green-500/20 text-green-400' :
                            status === 'warning' ? 'bg-yellow-500/20 text-yellow-400' :
                              'bg-red-500/20 text-red-400 animate-pulse')}>
                          {status}
                        </div>
                      </div>

                      <div className="space-y-2">
                        <div className="flex items-center justify-between text-xs">
                          <span className="text-indigo-300/60 uppercase text-[9px]">Live Count</span>
                          <span className="text-white font-mono font-bold leading-none">{venue.currentCrowd}</span>
                        </div>
                        <div className="h-1.5 bg-indigo-950 rounded-full overflow-hidden">
                          <motion.div
                            initial={{ width: 0 }}
                            animate={{ width: `${percentage}%` }}
                            className={cn("h-full",
                              status === 'safe' ? 'bg-green-500' :
                                status === 'warning' ? 'bg-yellow-500' :
                                  'bg-red-500')}
                          />
                        </div>
                        <div className="flex items-center justify-between text-[10px] font-medium leading-none">
                          <span className="text-indigo-200/50 italic">Full Cap: {venue.capacity}</span>
                          <span className={cn("font-bold",
                            status === 'safe' ? 'text-green-400' :
                              status === 'warning' ? 'text-yellow-400' :
                                'text-red-400')}>
                            {percentage}%
                          </span>
                        </div>
                      </div>
                    </div>
                  </div>
                </div>
              </motion.div>
            );
          })}
        </AnimatePresence>

        {/* Global Stats Overlay */}
        <div className="absolute bottom-4 left-4 right-4 flex justify-between items-end pointer-events-none">
          <div className="bg-indigo-950/60 backdrop-blur-md border border-indigo-400/20 p-2.5 rounded-lg flex items-center gap-3">
            <Info className="w-4 h-4 text-indigo-300 opacity-50" />
            <p className="text-[10px] text-indigo-100/70 font-medium">
              Hotspots updated in <span className="text-indigo-300 font-bold">real-time</span> based on entrance data.
            </p>
          </div>

          <div className="h-full flex flex-col items-end gap-1">
            <div className="px-3 py-1 bg-indigo-500/20 border border-indigo-500/40 rounded-md">
              <span className="text-[9px] font-black text-indigo-300 uppercase tracking-widest leading-none">Security Active</span>
            </div>
          </div>
        </div>
      </div>
    </motion.div>
  );
}

import { useMemo } from 'react';
import { motion } from 'framer-motion';
import { 
  LineChart, 
  Line, 
  XAxis, 
  YAxis, 
  CartesianGrid, 
  Tooltip, 
  ResponsiveContainer,
  Area,
  ComposedChart,
  Legend
} from 'recharts';
import { Brain, TrendingUp } from 'lucide-react';
import { generateHistoricalData } from '@/data/venueData';

export function PredictionChart() {
  const data = useMemo(() => generateHistoricalData(), []);

  const CustomTooltip = ({ active, payload, label }: any) => {
    if (!active || !payload?.length) return null;

    return (
      <div className="glass-card p-3 border border-border">
        <p className="text-xs text-muted-foreground mb-2">{label}</p>
        {payload.map((entry: any, index: number) => (
          <div key={index} className="flex items-center gap-2 text-sm">
            <div 
              className="w-2 h-2 rounded-full" 
              style={{ backgroundColor: entry.color }}
            />
            <span className="text-muted-foreground">{entry.name}:</span>
            <span className="font-semibold text-foreground">
              {entry.value ? entry.value.toLocaleString() : 'N/A'}
            </span>
          </div>
        ))}
      </div>
    );
  };

  return (
    <motion.div
      initial={{ opacity: 0, y: 20 }}
      animate={{ opacity: 1, y: 0 }}
      className="glass-card p-6"
    >
      <div className="flex items-center justify-between mb-6">
        <div className="flex items-center gap-3">
          <div className="w-10 h-10 rounded-lg bg-primary/10 flex items-center justify-center">
            <Brain className="w-5 h-5 text-primary" />
          </div>
          <div>
            <h3 className="font-semibold text-foreground">AI Crowd Prediction</h3>
            <p className="text-xs text-muted-foreground">Next 4 hours forecast</p>
          </div>
        </div>
        <div className="flex items-center gap-4 text-xs">
          <div className="flex items-center gap-2">
            <div className="w-3 h-0.5 bg-secondary rounded" />
            <span className="text-muted-foreground">Actual</span>
          </div>
          <div className="flex items-center gap-2">
            <div className="w-3 h-0.5 bg-primary rounded border-dashed" style={{ borderStyle: 'dashed' }} />
            <span className="text-muted-foreground">Predicted</span>
          </div>
        </div>
      </div>

      <div className="h-[250px]">
        <ResponsiveContainer width="100%" height="100%">
          <ComposedChart data={data}>
            <defs>
              <linearGradient id="actualGradient" x1="0" y1="0" x2="0" y2="1">
                <stop offset="0%" stopColor="hsl(187, 94%, 43%)" stopOpacity={0.3} />
                <stop offset="100%" stopColor="hsl(187, 94%, 43%)" stopOpacity={0} />
              </linearGradient>
              <linearGradient id="predictedGradient" x1="0" y1="0" x2="0" y2="1">
                <stop offset="0%" stopColor="hsl(263, 70%, 66%)" stopOpacity={0.2} />
                <stop offset="100%" stopColor="hsl(263, 70%, 66%)" stopOpacity={0} />
              </linearGradient>
            </defs>
            <CartesianGrid strokeDasharray="3 3" stroke="hsl(222, 47%, 15%)" />
            <XAxis 
              dataKey="time" 
              tick={{ fill: 'hsl(215, 20%, 65%)', fontSize: 10 }}
              axisLine={{ stroke: 'hsl(222, 47%, 15%)' }}
              tickLine={false}
            />
            <YAxis 
              tick={{ fill: 'hsl(215, 20%, 65%)', fontSize: 10 }}
              axisLine={{ stroke: 'hsl(222, 47%, 15%)' }}
              tickLine={false}
              tickFormatter={(value) => `${(value / 1000).toFixed(1)}k`}
            />
            <Tooltip content={<CustomTooltip />} />
            <Area
              type="monotone"
              dataKey="actual"
              stroke="transparent"
              fill="url(#actualGradient)"
            />
            <Line
              type="monotone"
              dataKey="actual"
              name="Actual"
              stroke="hsl(187, 94%, 43%)"
              strokeWidth={2}
              dot={false}
              activeDot={{ r: 4, fill: 'hsl(187, 94%, 43%)' }}
            />
            <Line
              type="monotone"
              dataKey="predicted"
              name="Predicted"
              stroke="hsl(263, 70%, 66%)"
              strokeWidth={2}
              strokeDasharray="5 5"
              dot={false}
              activeDot={{ r: 4, fill: 'hsl(263, 70%, 66%)' }}
            />
          </ComposedChart>
        </ResponsiveContainer>
      </div>

      <div className="mt-4 flex items-center gap-2 text-sm text-muted-foreground">
        <TrendingUp className="w-4 h-4 text-success" />
        <span>Peak expected at <strong className="text-foreground">8:00 PM</strong> with ~4,200 attendees</span>
      </div>
    </motion.div>
  );
}

import { motion, AnimatePresence } from 'framer-motion';
import { Scan, Plus, Minus, Smartphone, Camera, CameraOff, WifiOff } from 'lucide-react';
import { useFestival } from '@/context/FestivalContext';
import { Button } from '@/components/ui/button';
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from './ui/select';
import { useState, useEffect } from 'react';
import { Scanner } from '@yudiel/react-qr-scanner';
import { toast } from 'sonner';

import { VenueIcon } from './VenueIcon';

export function QRScanner() {
  const { venues, scanEntry, scanExit } = useFestival();
  const [selectedVenue, setSelectedVenue] = useState(venues[0]?.id || '');
  const [scanCount, setScanCount] = useState(0);
  const [mode, setMode] = useState<'real' | 'simulate'>('real');
  const [scanType, setScanType] = useState<'entry' | 'exit'>('entry');
  const [lastScanned, setLastScanned] = useState<string | null>(null);

  // Polish: Offline Detection
  const [isOnline, setIsOnline] = useState(navigator.onLine);
  // Polish: Secret Dev Mode
  const [devModeClicks, setDevModeClicks] = useState(0);
  const [isDevMode, setIsDevMode] = useState(false);

  useEffect(() => {
    const handleOnline = () => setIsOnline(true);
    const handleOffline = () => setIsOnline(false);
    window.addEventListener('online', handleOnline);
    window.addEventListener('offline', handleOffline);
    return () => {
      window.removeEventListener('online', handleOnline);
      window.removeEventListener('offline', handleOffline);
    };
  }, []);

  const handleDevClick = () => {
    const newCount = devModeClicks + 1;
    setDevModeClicks(newCount);
    if (newCount === 5) {
      setIsDevMode(true);
      toast.info("Developer Mode Enabled: Simulation Active");
    }
  };

  const handleScan = (type: 'entry' | 'exit', data?: string) => {
    if (!selectedVenue) {
      toast.error('Please select a venue first');
      return;
    }

    // If data is provided (from QR), try to parse it
    let studentInfo = data;
    if (data) {
      try {
        const parsed = JSON.parse(data);
        studentInfo = parsed.name || parsed.sid || data;
      } catch (e) {
        // Not JSON, use raw string
      }
    }

    if (type === 'entry') {
      scanEntry(selectedVenue);
      toast.success(`Entry Scanned${studentInfo ? `: ${studentInfo}` : ''}`);
    } else {
      scanExit(selectedVenue);
      toast.info(`Exit Scanned${studentInfo ? `: ${studentInfo}` : ''}`);
    }
    setScanCount(prev => prev + 1);
    setLastScanned(studentInfo || 'Manual Scan');

    // Haptic feedback
    if (navigator.vibrate) {
      navigator.vibrate(type === 'entry' ? 50 : [50, 50, 50]);
    }
  };

  const onQrDecode = (result: any) => {
    const rawValue = result?.[0]?.rawValue;
    if (rawValue) {
      handleScan(scanType, rawValue);
    }
  };

  const selectedVenueData = venues.find(v => v.id === selectedVenue);

  return (
    <motion.div
      initial={{ opacity: 0, y: 20 }}
      animate={{ opacity: 1, y: 0 }}
      className="glass-card p-6 relative overflow-hidden"
    >
      {/* Offline Banner */}
      <AnimatePresence>
        {!isOnline && (
          <motion.div
            initial={{ height: 0, opacity: 0 }}
            animate={{ height: 'auto', opacity: 1 }}
            exit={{ height: 0, opacity: 0 }}
            className="bg-destructive/15 text-destructive text-xs font-semibold p-2 rounded-t-lg flex items-center justify-center gap-2 mb-4 border border-destructive/20"
          >
            <WifiOff className="w-3 h-3" />
            OFFLINE MODE - Queuing Scans
          </motion.div>
        )}
      </AnimatePresence>

      <div className="flex items-center justify-between mb-6">
        <div className="flex items-center gap-3">
          <div
            className={`w-10 h-10 rounded-lg flex items-center justify-center cursor-pointer transition-colors ${mode === 'real' ? 'bg-purple-500/10 text-purple-500' : 'bg-primary/10 text-primary'}`}
            onClick={handleDevClick}
          >
            {mode === 'real' ? <Camera className="w-5 h-5" /> : <Smartphone className="w-5 h-5" />}
          </div>
          <div>
            <h3 className="font-semibold text-foreground select-none" onClick={handleDevClick}>
              {mode === 'real' ? 'Live Scanner' : 'Simulate Scan'}
            </h3>
            <p className="text-xs text-muted-foreground">
              {mode === 'real' ? 'Auto-detects QR codes' : 'Manual entry/exit buttons'}
            </p>
          </div>
        </div>

        {/* Only show toggle if Dev Mode is active */}
        {isDevMode && (
          <div className="flex gap-2">
            <Button
              size="sm"
              variant={mode === 'simulate' ? 'default' : 'outline'}
              onClick={() => setMode('simulate')}
              className="text-xs h-8"
            >
              Sim
            </Button>
            <Button
              size="sm"
              variant={mode === 'real' ? 'default' : 'outline'}
              onClick={() => setMode('real')}
              className="text-xs h-8"
            >
              Cam
            </Button>
          </div>
        )}
      </div>

      {/* Venue Selector */}
      <div className="mb-6">
        <label className="text-xs text-muted-foreground mb-2 block">Select Venue</label>
        <Select value={selectedVenue} onValueChange={setSelectedVenue}>
          <SelectTrigger className="bg-muted/50 border-border">
            <SelectValue placeholder="Choose venue" />
          </SelectTrigger>
          <SelectContent>
            {venues.map(venue => (
              <SelectItem key={venue.id} value={venue.id}>
                <span className="flex items-center gap-2">
                  <VenueIcon icon={venue.icon} className="w-4 h-4" />
                  <span>{venue.name}</span>
                </span>
              </SelectItem>
            ))}
          </SelectContent>
        </Select>
      </div>

      {/* Current Count */}
      {selectedVenueData && (
        <div className="text-center mb-6">
          <p className="text-xs text-muted-foreground mb-1">Current at {selectedVenueData.name}</p>
          <p className={`text-4xl font-bold ${scanType === 'entry' ? 'text-green-500' : 'text-red-500'} transition-colors`}>
            {selectedVenueData.currentCrowd.toLocaleString()}
          </p>
        </div>
      )}

      {/* Real Scanner Mode */}
      {mode === 'real' && (
        <div className="mb-6 space-y-4">
          <div className="flex gap-2 p-1 bg-muted/50 rounded-lg">
            <button
              className={`flex-1 py-2 text-xs font-medium rounded-md transition-all ${scanType === 'entry' ? 'bg-green-500 text-white shadow-lg' : 'text-muted-foreground hover:bg-background/50'}`}
              onClick={() => setScanType('entry')}
            >
              ENTRY MODE
            </button>
            <button
              className={`flex-1 py-2 text-xs font-medium rounded-md transition-all ${scanType === 'exit' ? 'bg-red-500 text-white shadow-lg' : 'text-muted-foreground hover:bg-background/50'}`}
              onClick={() => setScanType('exit')}
            >
              EXIT MODE
            </button>
          </div>

          <div className="relative aspect-square rounded-xl overflow-hidden border-2 border-primary/20 bg-black">
            <Scanner
              onScan={onQrDecode}
              components={{
                onOff: true,
                torch: true,
                zoom: true,
                finder: true
              }}
              allowMultiple={true}
              scanDelay={500}
            />
            <div className="absolute inset-x-0 bottom-0 p-2 bg-black/60 text-white text-[10px] text-center">
              Point at a QR Code
            </div>
          </div>
        </div>
      )}

      {/* Simulation Mode - Only shown if active */}
      {mode === 'simulate' && (
        <div className="grid grid-cols-2 gap-4 mb-4">
          <Button
            onClick={() => handleScan('exit')}
            variant="outline"
            className="h-16 flex flex-col items-center gap-1 border-border hover:bg-danger/10 hover:text-danger hover:border-danger/50"
          >
            <Minus className="w-5 h-5" />
            <span className="text-xs">Exit</span>
          </Button>
          <Button
            onClick={() => handleScan('entry')}
            className="h-16 flex flex-col items-center gap-1 bg-gradient-brand hover:opacity-90"
          >
            <Plus className="w-5 h-5" />
            <span className="text-xs">Entry</span>
          </Button>
        </div>
      )}

      {/* Scan Counter */}
      <div className="text-center mt-4">
        <p className="text-xs text-muted-foreground">
          Session scans: <span className="font-semibold text-foreground">{scanCount}</span>
        </p>
        {lastScanned && (
          <p className="text-xs text-primary mt-1 animate-pulse">
            Last: {lastScanned}
          </p>
        )}
      </div>
    </motion.div>
  );
}

